/*Table structure for table `travelnotecomment` */

DROP TABLE IF EXISTS `travelnotecomment`;

CREATE TABLE `travelnotecomment` (
  `commentId` int(11) NOT NULL COMMENT '评论id',
  `userId` int(11) NOT NULL COMMENT '用户id',
